--  DDL for View ACCESORIOS_CON_MENOS_DE_5_UNIDADES
--------------------------------------------------------

  CREATE OR REPLACE FORCE NONEDITIONABLE VIEW "USER_ASSIGNMENT2"."ACCESORIOS_CON_MENOS_DE_5_UNIDADES" ("ID", "DESCRIPCION", "CODIGO_FABRICANTE", "NOMBRE_FABRICANTE", "UNIDADES_DISPONIBLES") AS 
  SELECT A.ID, A.DESCRIPCION, F.CODIGO_FABRICANTE, F.NOMBRE_FABRICANTE, A.UNIDADES_DISPONIBLES
  FROM ACCESORIOS A INNER JOIN FABRICANTES F
   ON A.ID_FABRICANTE = F.ID
    WHERE A.UNIDADES_DISPONIBLES < 5;
